import React from 'react';

const BrochureItem = ({ title }) => {
  const brochures = [
    { title: 'Çevre Uygulamaları E Serisi Broşürü', qrName: 'Çevre_Uygulamalari_E_Serisi_Brosuru.png', pdfUrl: '/pdfs/Çevre_Uygulamalari_E_Serisi_Brosuru.pdf' },
    { title: 'Gıda Uygulamaları F Serisi Broşürü', qrName: 'Gida_Uygulamalari_F_Serisi_Brosuru.png', pdfUrl: '/pdfs/Gida_Uygulamalari_F_Serisi_Brosuru.pdf' },
    { title: 'Endüstri Uygulamaları I Serisi Broşürü', qrName: 'Endustri_Uygulamalari_I_Serisi_Brosuru.png', pdfUrl: '/pdfs/Endustri_Uygulamalari_I_Serisi_Brosuru.pdf' },
    { title: 'HAUS Dekantörler', qrName: 'HAUS_Dekantorler.png', pdfUrl: '/pdfs/HAUS_Dekantorler.pdf' },
    { title: 'HAUS Süt Sepratörleri', qrName: 'HAUS_Sut_Sepratorleri.png', pdfUrl: '/pdfs/HAUS_Sut_Sepratorleri.pdf' },
    { title: 'Olive Plus 33 Broşürü', qrName: 'Olive_Plus_33_Brosuru.png', pdfUrl: '/pdfs/Olive_Plus_33_Brosuru.pdf' },
    { title: 'Olive Plus 52 Broşürü', qrName: 'Olive_Plus_52_Brosuru.png', pdfUrl: '/pdfs/Olive_Plus_52_Brosuru.pdf' },
    { title: 'Olive Plus 53 Broşürü', qrName: 'Olive_Plus_53_Brosuru.png', pdfUrl: '/pdfs/Olive_Plus_53_Brosuru.pdf' },
    { title: 'Olive Plus 55 Broşürü', qrName: 'Olive_Plus_55_Brosuru.png', pdfUrl: '/pdfs/Olive_Plus_55_Brosuru.pdf' },
    { title: 'Olive Plus 64 Broşürü', qrName: 'Olive_Plus_64_Brosuru.png', pdfUrl: '/pdfs/Olive_Plus_64_Brosuru.pdf' },
    { title: 'Olive Pro 21 Broşürü', qrName: 'Olive_Pro_21_Brosuru.png', pdfUrl: '/pdfs/Olive_Pro_21_Brosuru.pdf' },
    { title: 'Olive Pro 31 Broşürü', qrName: 'Olive_Pro_31_Brosuru.png', pdfUrl: '/pdfs/Olive_Pro_31_Brosuru.pdf' },
    { title: 'HAUS Turbo Blower Broşürü', qrName: 'HAUS_Turbo_Blower_Brosuru.png', pdfUrl: '/pdfs/HAUS_Turbo_Blower_Brosuru.pdf' },
    { title: 'HAUS Vidalı Pres Broşürü', qrName: 'HAUS_Vidali_Pres_Brosuru.png', pdfUrl: '/pdfs/HAUS_Vidali_Pres_Brosuru.pdf' },
    { title: 'HAUS Separatörler', qrName: 'HAUS_Separatorler.png', pdfUrl: '/pdfs/HAUS_Separatorler.pdf' },
    { title: 'Environment Applications · E Series', qrName: 'Environment_Applications_·_E_Series.png', pdfUrl: '/pdfs/Environment_Applications_E_Series.pdf' },
    { title: 'Food Applications · F Series', qrName: 'Food_Applications_·_F_Series.png', pdfUrl: '/pdfs/Food_Applications_F_Series.pdf' },
    { title: 'Industrial Applications · I Series', qrName: 'Industrial_Applications_·_I_Series.png', pdfUrl: '/pdfs/Industrial_Applications_I_Series.pdf' },
    { title: 'HAUS Decanters', qrName: 'HAUS_Decanters.png', pdfUrl: '/pdfs/HAUS_Decanters.pdf' },
    { title: 'HAUS Dairy Separators', qrName: 'HAUS_Dairy_Separators.png', pdfUrl: '/pdfs/HAUS_Dairy_Separators.pdf' },
    { title: 'HAUS Turbo Blower', qrName: 'HAUS_Turbo_Blower.png', pdfUrl: '/pdfs/HAUS_Turbo_Blower.pdf' },
    { title: 'HAUS Screw Press', qrName: 'HAUS_Screw_Press.png', pdfUrl: '/pdfs/HAUS_Screw_Press.pdf' },
    { title: 'HAUS Separators', qrName: 'HAUS_Separators.png', pdfUrl: '/pdfs/HAUS_Separators.pdf' },
  ];

  const findQRCode = (title) => {
    const brochure = brochures.find(b => b.title === title);
    return brochure ? `/images/QR_code/${brochure.qrName}` : null;
  };

  const findPdfUrl = (title) => {
    const brochure = brochures.find(b => b.title === title);
    return brochure ? brochure.pdfUrl : null;
  };

  const qrImageUrl = findQRCode(title);
  const pdfUrl = findPdfUrl(title);

  return (
    <div className="brochure-item">
      <h3 className="brochure-title">{title}</h3>
      {qrImageUrl && (
        <div className="qr-code">
          <img src={qrImageUrl} alt={`${title} QR Code`} />
        </div>
      )}
      <button className="download-button" onClick={() => window.open(pdfUrl, '_blank')}>İndir</button>
    </div>
  );
};

export default BrochureItem;
